(function($) { // Encapsulate
	function GetCategoryImageUrl(category) {
		var categoryImgUrl = "";
				
		switch (category) {
			case "TV: STD":
				categoryImgUrl = "data:image/png;base64,";
				break;
				
			case "TV: HD":
				categoryImgUrl = "data:image/gif;base64,";
				break;
				
			case "Movies: HD":
				categoryImgUrl = "data:image/gif;base64,";
				break;
				
			case "Movies: STD":
				categoryImgUrl = "data:image/gif;base64,";
				break;
				
			case "Movies: DVD":
				categoryImgUrl = "data:image/gif;base64,";
				break;
				
			case "Games: PC":
				categoryImgUrl = "data:image/gif;base64,";
				break;
				
			case "Games: PS3":
				categoryImgUrl = "data:image/gif;base64,";
				break;
				
			case "Games: xbox360":
				categoryImgUrl = "data:image/gif;base64,";
				break;				
				
			case "Apps: PC":
				categoryImgUrl = "data:image/gif;base64,";
				break;
				
			case "Apps: MAC":
				categoryImgUrl = "data:image/gif;base64,";
				break;		
				
			case "Music: MP3":
				categoryImgUrl = "data:image/gif;base64,";
				break;
				
			case "Music: FLAC":
				categoryImgUrl = "data:image/gif;base64,";
				break;
				
			case "Music: MVID":
				categoryImgUrl = "data:image/gif;base64,";
				break;				
				
			default:
				categoryImgUrl = "data:image/gif;base64,";
				break;
		}
		
		return categoryImgUrl;
	}
	
	$(".nzbt_type").each(function (i) {
		var link = $(this).children("a.linky");
		var category = $(link).html();		
	
		var imgHtml = "<img src='" + GetCategoryImageUrl(category) + "' alt='" + category + "' title='" + category + "' />";
		$(link).html(imgHtml);
	});	
	
})(jQuery);